schemas:
DEFINE TABLE rzd_tasks SCHEMAFULL;

DEFINE FIELD created_at ON rzd_tasks VALUE time::now() READONLY;
DEFINE FIELD type ON rzd_tasks TYPE string;
DEFINE FIELD data ON rzd_tasks TYPE object FLEXIBLE;
DEFINE FIELD user ON rzd_tasks TYPE record<users>;

DEFINE TABLE script_migration SCHEMAFULL
    PERMISSIONS
        FOR select FULL
        FOR create, update, delete NONE;

DEFINE FIELD script_name ON script_migration TYPE string;
DEFINE FIELD executed_at ON script_migration TYPE datetime DEFAULT time::now();
DEFINE TABLE tokens SCHEMAFULL;

DEFINE FIELD created_at ON tokens VALUE time::now() READONLY;
DEFINE FIELD token ON tokens TYPE uuid;
DEFINE FIELD user ON tokens TYPE record<users>;

DEFINE TABLE users SCHEMAFULL;

DEFINE FIELD username ON users TYPE string;
DEFINE FIELD email ON users TYPE string;
DEFINE FIELD password ON users TYPE string;
DEFINE FIELD created_at ON users VALUE time::now() READONLY;
DEFINE FIELD is_verified ON users TYPE bool DEFAULT false;
DEFINE FIELD role ON users TYPE string DEFAULT 'user';

DEFINE INDEX users_username_index ON users COLUMNS username UNIQUE;
DEFINE INDEX users_email_index ON users COLUMNS email UNIQUE;

DEFINE TABLE verify_tokens SCHEMAFULL;

DEFINE FIELD created_at ON verify_tokens VALUE time::now() READONLY;
DEFINE FIELD valid_until ON verify_tokens TYPE datetime;
DEFINE FIELD token ON verify_tokens TYPE uuid;
DEFINE FIELD user ON verify_tokens TYPE record<users>;
